import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = () => {
  const [rotas, setRotas] = useState([]); // Estado para armazenar as rotas
  const [rota, setRota] = useState("");
  const [resultado, setResultado] = useState(null);

  // Carregar rotas existentes do backend
  useEffect(() => {
    axios
      .get("http://localhost:5283/api/Rota") // Endpoint do backend
      .then((response) => setRotas(response.data)) // Salva as rotas no estado
      .catch((error) => console.error("Erro ao carregar rotas:", error)); // Log de erro
  }, []);

  // Consulta específica de rota
  const handleConsulta = (e) => {
    e.preventDefault();
    const [origem, destino] = rota.split("-");
    axios
      .get(`http://localhost:5283/api/Rota?origem=${origem}&destino=${destino}`)
      .then((response) => setResultado(response.data))
      .catch((error) => console.error("Erro ao buscar rota:", error));
  };

  return (
    <div className="home">
      <h1>Consulta de Melhor Rota</h1>
      <form onSubmit={handleConsulta}>
        <label>Digite a rota (formato DE-PARA):</label>
        <input
          type="text"
          value={rota}
          onChange={(e) => setRota(e.target.value)}
          placeholder="Ex: GRU-CDG"
          required
        />
        <button type="submit">Consultar</button>
      </form>
      {resultado && (
        <div className="resultado">
          <h2>Melhor Rota:</h2>
          <p>
            {resultado.rota.join(" - ")} ao custo de ${resultado.custo}
          </p>
        </div>
      )}
      <div className="lista-rotas" style={{ marginTop: "2rem" }}>
        <h2>Rotas Registradas</h2>
        {rotas.length === 0 ? (
          <p>Nenhuma rota encontrada.</p>
        ) : (
          <ul>
            {rotas.map((rota) => (
              <li key={rota.id}>
                {rota.origem} - {rota.destino} ao custo de ${rota.valor}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Home;
