import React, { useState } from "react";
import axios from "axios";

const Home = () => {
  const [rota, setRota] = useState("");
  const [resultado, setResultado] = useState(null);

  const handleConsulta = (e) => {
    e.preventDefault();
    const [origem, destino] = rota.split("-");
    axios
      .get(`http://localhost:5283/api/rota?origem=${origem}&destino=${destino}`)
      .then((response) => setResultado(response.data))
      .catch((error) => console.error("Erro ao buscar rota:", error));
  };

  return (
    <div className="home">
      <h1>Consulta de Melhor Rota</h1>
      <form onSubmit={handleConsulta}>
        <label>Digite a rota (formato DE-PARA):</label>
        <input
          type="text"
          value={rota}
          onChange={(e) => setRota(e.target.value)}
          placeholder="Ex: GRU-CDG"
          required
        />
        <button type="submit">Consultar</button>
      </form>
      {resultado && (
        <div className="resultado">
          <h2>Melhor Rota:</h2>
          <p>
            {resultado.rota.join(" - ")} ao custo de ${resultado.custo}
          </p>
        </div>
      )}
    </div>
  );
};

export default Home;
