import React, { useState, useEffect } from "react";
import api from "../services/api";

function ConsultaRotas() {
  const [rotas, setRotas] = useState([]);
  const [editando, setEditando] = useState(null);
  const [novaOrigem, setNovaOrigem] = useState("");
  const [novoDestino, setNovoDestino] = useState("");
  const [novoValor, setNovoValor] = useState("");

  // Carregar todas as rotas
  useEffect(() => {
    const fetchRotas = async () => {
      try {
        const response = await api.get("/rota");
        setRotas(response.data);
      } catch (error) {
        console.error("Erro ao carregar rotas:", error.message);
        alert("Erro ao carregar rotas.");
      }
    };
    fetchRotas();
  }, []);

  // Atualizar uma rota
  const handleUpdate = async (id) => {
    try {
      await api.put(`/rota/${id}`, {
        origem: novaOrigem,
        destino: novoDestino,
        valor: novoValor,
      });
      alert("Rota atualizada com sucesso!");
      setEditando(null);
      window.location.reload(); // Recarregar as rotas
    } catch (error) {
      console.error("Erro ao atualizar rota:", error.message);
      alert("Erro ao atualizar rota.");
    }
  };

  // Excluir uma rota
  const handleDelete = async (id) => {
    try {
      await api.delete(`/rota/${id}`);
      alert("Rota removida com sucesso!");
      setRotas(rotas.filter((rota) => rota.id !== id));
    } catch (error) {
      console.error("Erro ao excluir rota:", error.message);
      alert("Erro ao excluir rota.");
    }
  };

  return (
    <div className="consulta-rotas">
      <h1>Rotas Registradas</h1>
      {rotas.length === 0 ? (
        <p>Nenhuma rota encontrada.</p>
      ) : (
        <ul>
          {rotas.map((rota) => (
            <li key={rota.id}>
              {editando === rota.id ? (
                <div>
                  <input
                    type="text"
                    placeholder="Nova Origem"
                    value={novaOrigem}
                    onChange={(e) => setNovaOrigem(e.target.value)}
                  />
                  <input
                    type="text"
                    placeholder="Novo Destino"
                    value={novoDestino}
                    onChange={(e) => setNovoDestino(e.target.value)}
                  />
                  <input
                    type="number"
                    placeholder="Novo Valor"
                    value={novoValor}
                    onChange={(e) => setNovoValor(e.target.value)}
                  />
                  <button onClick={() => handleUpdate(rota.id)}>Salvar</button>
                  <button onClick={() => setEditando(null)}>Cancelar</button>
                </div>
              ) : (
                <>
                  {rota.origem} - {rota.destino} ao custo de ${rota.valor}
                  <button onClick={() => setEditando(rota.id)}>Editar</button>
                  <button onClick={() => handleDelete(rota.id)}>Excluir</button>
                </>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default ConsultaRotas;
