import React, { useState } from "react";
import api from "../services/api";

const Home = () => {
  const [rota, setRota] = useState("");
  const [resultado, setResultado] = useState(null);

  const handleConsulta = async (e) => {
    e.preventDefault();
    const [origem, destino] = rota.split("-");
    if (!origem || !destino) {
      alert("Formato inválido! Use DE-PARA (ex: GRU-CDG).");
      return;
    }
    try {
      const response = await api.get(`/rota/melhor?origem=${origem}&destino=${destino}`);
      setResultado(response.data);
    } catch (error) {
      console.error("Erro ao buscar rota:", error.message);
      alert("Erro ao consultar rota. Verifique os dados.");
    }
  };

  return (
    <div className="home">
      <h1>Consulta de Melhor Rota</h1>
      <form onSubmit={handleConsulta}>
        <label>Digite a rota (formato DE-PARA):</label>
        <input
          type="text"
          value={rota}
          onChange={(e) => setRota(e.target.value)}
          placeholder="Ex: GRU-CDG"
          required
        />
        <button type="submit">Consultar</button>
      </form>
      {resultado && (
        <div className="resultado">
          <h2>Melhor Rota:</h2>
          <p>
            {resultado.rota.join(" - ")} ao custo de ${resultado.custo}
          </p>
        </div>
      )}
    </div>
  );
};

export default Home;
