import React, { useState } from "react";
import axios from "axios";

const AddRota = () => {
  const [origem, setOrigem] = useState("");
  const [destino, setDestino] = useState("");
  const [valor, setValor] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5283/api/rota", { origem, destino, valor })
      .then(() => {
        alert("Rota adicionada com sucesso!");
        setOrigem("");
        setDestino("");
        setValor("");
      })
      .catch((error) => console.error("Erro ao adicionar rota:", error));
  };

  return (
    <div className="add-rota">
      <h1>Adicionar Nova Rota</h1>
      <form onSubmit={handleSubmit}>
        <label>Origem:</label>
        <input
          type="text"
          value={origem}
          onChange={(e) => setOrigem(e.target.value)}
          placeholder="Ex: GRU"
          required
        />
        <label>Destino:</label>
        <input
          type="text"
          value={destino}
          onChange={(e) => setDestino(e.target.value)}
          placeholder="Ex: CDG"
          required
        />
        <label>Valor:</label>
        <input
          type="number"
          value={valor}
          onChange={(e) => setValor(e.target.value)}
          placeholder="Ex: 40"
          required
        />
        <button type="submit">Adicionar</button>
      </form>
    </div>
  );
};

export default AddRota;
