import React, { useState } from "react";
import axios from "axios";

function AddRota() {
  const [origem, setOrigem] = useState("");
  const [destino, setDestino] = useState("");
  const [valor, setValor] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5283/api/rota", { origem, destino, valor })
      .then(() => alert("Rota adicionada com sucesso!"))
      .catch((err) => console.error("Erro ao adicionar rota:", err));
  };

  return (
    <div style={{ textAlign: "center", marginTop: "2rem" }}>
      <h1>Adicionar Nova Rota</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Origem"
          value={origem}
          onChange={(e) => setOrigem(e.target.value)}
          required
          style={{ margin: "0.5rem" }}
        />
        <input
          type="text"
          placeholder="Destino"
          value={destino}
          onChange={(e) => setDestino(e.target.value)}
          required
          style={{ margin: "0.5rem" }}
        />
        <input
          type="number"
          placeholder="Valor"
          value={valor}
          onChange={(e) => setValor(e.target.value)}
          required
          style={{ margin: "0.5rem" }}
        />
        <button type="submit" style={{ margin: "0.5rem" }}>
          Adicionar Rota
        </button>
      </form>
    </div>
  );
}

export default AddRota;
